{
    "$schema": "http://json-schema.org/draft-07/schema",
    "$id": "http://example.com/example.json",
    "type": "object",
    "title": "The root schema",
    "description": "The root schema comprises the entire JSON document.",
    "examples": [
        {
            "firstname": "Miguel",
            "lastname": "Rohan",
            "totalprice": 206,
            "depositpaid": true,
            "bookingdates": {
                "checkin": "2021-04-17",
                "checkout": "2022-10-15"
            },
            "additionalneeds": "hard drive"
        }
    ],
    "required": [
        "firstname",
        "lastname",
        "totalprice",
        "depositpaid",
        "bookingdates"
    ],
    "properties": {
        "firstname": {
            "$id": "#/properties/firstname",
            "type": "string",
            "title": "The firstname schema",
            "description": "The first name used when booking was created.",
            "default": "",
            "examples": [
                "Miguel"
            ]
        },
        "lastname": {
            "$id": "#/properties/lastname",
            "type": "string",
            "title": "The lastname schema",
            "description": "The last name used when booking was created.",
            "default": "",
            "examples": [
                "Rohan"
            ]
        },
        "totalprice": {
            "$id": "#/properties/totalprice",
            "type": "integer",
            "title": "The totalprice schema",
            "description": "The total price used when booking was created.",
            "default": 0,
            "examples": [
                206
            ]
        },
        "depositpaid": {
            "$id": "#/properties/depositpaid",
            "type": "boolean",
            "title": "The depositpaid schema",
            "description": "The deposit paid indicator used when booking was created.",
            "default": false,
            "examples": [
                true
            ]
        },
        "bookingdates": {
            "$id": "#/properties/bookingdates",
            "type": "object",
            "title": "The bookingdates schema",
            "description": "Check in and check out dates used when booking was created.",
            "default": {},
            "examples": [
                {
                    "checkin": "2021-04-17",
                    "checkout": "2022-10-15"
                }
            ],
            "required": [
                "checkin",
                "checkout"
            ],
            "properties": {
                "checkin": {
                    "$id": "#/properties/bookingdates/properties/checkin",
                    "type": "string",
                    "title": "The checkin schema",
                    "description": "Check in date in YYYY-MM-DD format when booking was created.",
                    "default": "",
                    "examples": [
                        "2021-04-17"
                    ]
                },
                "checkout": {
                    "$id": "#/properties/bookingdates/properties/checkout",
                    "type": "string",
                    "title": "The checkout schema",
                    "description": "Check out date in YYYY-MM-DD format when booking was created.",
                    "default": "",
                    "examples": [
                        "2022-10-15"
                    ]
                }
            },
            "additionalProperties": false
        },
        "additionalneeds": {
            "$id": "#/properties/additionalneeds",
            "type": "string",
            "title": "The additionalneeds schema",
            "description": "An optional property to indicate additional needs when booking was created.",
            "default": "",
            "examples": [
                "hard drive"
            ]
        }
    },
    "additionalProperties": false
}

